definition.script:
node('docker.local.jenkins.slave') {
    timestamps {
       def mvnHome
       deleteDir()

       stage('Preparation') {
           mvnHome = tool 'maven'
          git 'https://github.com/prayagupd/eccount-rest.git'
       }
       stage('Build') {
          sh "'${mvnHome}/bin/mvn' -Dmaven.test.failure.ignore clean package"
       }
       stage('Results') {
          archiveArtifacts 'target/*.war'
       }
    }
}
